import React, { useState, useEffect } from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [form, setForm] = useState({ name: "", subject: "", rating: "", comments: "" });
  const [feedbackList, setFeedbackList] = useState([]);
  const [editId, setEditId] = useState(null);


  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const submitFeedback = async (e) => {
  e.preventDefault();
  if (editId) {
    await axios.put(`http://localhost:5000/update-feedback/${editId}`, form);
    setEditId(null);
  } else {
    await axios.post("http://localhost:5000/submit-feedback", form);
  }
  alert("Feedback submitted");
  setForm({ name: "", subject: "", rating: "", comments: "" });
  getFeedback();
};


  const getFeedback = async () => {
    const res = await axios.get("http://localhost:5000/get-feedback");
    setFeedbackList(res.data);
  };

  useEffect(() => {
    getFeedback();
  }, []);

  const deleteFeedback = async (id) => {
  if (window.confirm("Are you sure you want to delete this feedback?")) {
    await axios.delete(`http://localhost:5000/delete-feedback/${id}`);
    getFeedback(); // refresh the list
  }
};



  return (
    <div className="container mt-5">
      <h2 className="mb-4">Student Feedback Form</h2>
      <form onSubmit={submitFeedback} className="border p-4 rounded shadow">
        <div className="mb-3">
          <input className="form-control" name="name" placeholder="Your Name" value={form.name} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <input className="form-control" name="subject" placeholder="Subject" value={form.subject} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <input type="number" className="form-control" name="rating" placeholder="Rating (1–5)" value={form.rating} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <textarea className="form-control" name="comments" placeholder="Comments" value={form.comments} onChange={handleChange}></textarea>
        </div>
        <button className="btn btn-primary" type="submit">Submit</button>
      </form>

      <h4 className="mt-5">Feedback Received</h4>
      <ul className="list-group mt-3">
        {feedbackList.map(fb => (
          <li key={fb.id} className="list-group-item d-flex justify-content-between align-items-start">
            <div>
              <strong>{fb.name}</strong> on <em>{fb.subject}</em> — ⭐ {fb.rating}<br />
              <small>{fb.comments}</small>
            </div>
            <button className="btn btn-danger btn-sm" onClick={() => deleteFeedback(fb.id)}>Delete</button>
          </li>
        ))}
      </ul>

    </div>
  );
}

export default App;
