import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";


const navigate = useNavigate();


function AdminDashboard() {
  const [feedbackList, setFeedbackList] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");


  const getFeedback = async () => {
    const res = await axios.get("http://localhost:5000/get-feedback");
    setFeedbackList(res.data);
  };

  const deleteFeedback = async (id) => {
    if (window.confirm("Delete this feedback?")) {
      await axios.delete(`http://localhost:5000/delete-feedback/${id}`);
      getFeedback(); // refresh
    }
  };

  useEffect(() => {
    getFeedback();
  }, []);

  return (
    <div className="container mt-5">
      <h2>Admin Dashboard</h2>
      <input
        type="text"
        className="form-control mb-3"
        placeholder="Search by name or subject..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value.toLowerCase())}
        />

      <p className="text-muted">All student feedback</p>

      <div className="text-end mb-3">
        <button className="btn btn-outline-danger btn-sm" onClick={() => navigate("/admin")}>
             Logout
        </button>
      </div>


      <ul className="list-group mt-4">
        {feedbackList.map((fb) => (
          <li key={fb.id} className="list-group-item d-flex justify-content-between align-items-start">
            <div>
              <strong>{fb.name}</strong> on <em>{fb.subject}</em> — ⭐ {fb.rating}<br />
              <small>{fb.comments}</small><br />
              {fb.timestamp && (
                <small className="text-muted">Submitted: {new Date(fb.timestamp).toLocaleString()}</small>
              )}
            </div>
            <button className="btn btn-sm btn-danger" onClick={() => deleteFeedback(fb.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminDashboard;
